#include <wchar.h>  // wchar_t *wcscpy(wchar_t * restrict dest,
                    //                 const wchar_t * restrict src);
                    // errno_t wcscpy_s(wchar_t * restrict dest,
                    //                  rsize_t destmax,
                    //                  const wchar_t * restrict src);
#include <stdio.h>

int main()
{
    struct record {
      wchar_t name[64];
