    count = wscanf( L"%%11l[rwxsStTld-+]%%u%%u%%u%%lu%%*10s%%*5s%%256ls",
                    perms, &links, &ownerid, &groupid, &size, name );

wprintf(L"%%ls %%u %%u %%u %%lu %%ls\n", 
