int th_func(void * arg)         // The thread function.
{
    puts("Hello from th_func ...");
    ++*(int*)arg;
    return 0;
}

int main()
{
    thrd_t th; int n = 1;

    if ( thrd_create(&th, th_func, &n) != thrd_success) {
       fprintf(stderr, "Error creating thread.\n"); return -1;
