    size_t result = mbstowcs( wmyname, argv[0], 256 );
    if ( result == -1 )
       return -1;

    wchar_t *mybasename = wcsrchr( wmyname, L'/' ); // End of path
    if ( mybasename != NULL )
      mybasename++;
    else
      mybasename = wmyname;

    wprintf( L"This program was invoked as %%ls.\n", mybasename );
}

