        n += wctomb( mbbuffer, wcs[i] );
    } while ( wcs[i++] != L'\0' );

    printf( "The wide string \"%%ls\" \nis %%u wide characters long.\n"
            "Its multibyte representation requires a buffer of %%u bytes.\n",
            wcs, wcslen( wcs ), n );

