/**
 * ******************************************************
 *  Sets the processor niceness:
 *  -20 to 19
 * ******************************************************
**/
void wrapper_nice() {
    int ret, val;

    /* get current nice value */
    val = nice (0);

    /* we want a nice value of 10 */
    val = 10 - val;
    errno = 0;
    ret = nice (val);
    if (ret == -1 && errno != 0)
            perror ("nice");
    else
            printf ("nice value is now %%d\n", ret);
}

