/**
 * ******************************************************
 *  Reads a line of data. Also stops reading at delimiter 
 *  'd'
 * ******************************************************
**/
void fgets_substitute(int n, char d, char* str, FILE *stream) {
    char *s;
    int c;

    s = str;

    while (--n > 0 && (c = fgetc(stream)) != EOF && (*s++ = c) != d)
        ;

    if (c == d)
        *--s = '\0';
    else
        *s ='\0' ;
}


