  public int print(Graphics g, PageFormat Pf, int pageIndex)
      throws PrinterException 
  {
    if (pageIndex > 0) return NO_SUCH_PAGE;
    Graphics2D g2 = (Graphics2D)g;
    g2.setFont(sFont);
    g2.setPaint(Color.black);
